/*非首页顶端当前目录树*/
body > header > .content > div > .lfe-caption
{
    visibility: hidden;
}

/*针对标题栏*/
#app-header
{
    height: 4em !important;
    z-index: 11 !important;
    position: sticky;
    top: 0%;
}
header .bg-wrap
{
    width: 100%;
    background-color: #29db2d;
}

.content header
{
    background-color: #f00;
}

header h1
{
    transition: none;
    font-size: 25px !important;/*字号*/
    margin: -2% 20% !important;/*文字相对按钮坐标*/
    width: 1000px;
    color: #ececec;/*文字颜色*/
}

#app-header > .content > .width-wrap/*min-margin*/ > .bread-crumb
{
    margin: 35px 100px !important;
}

/*某不可见神秘方框*/
#app-header > .content > .width-wrap/*min-margin*/ > .functional
{
    margin: -1000% 0%;
}

/*非首页标题右上框*/
.user-nav
{
    transition: all 0.2s linear 0s;
    width: 24%;
    height: 69px;
    margin: 2% -5.5%;
}
.user-nav:hover
{
    transition: all 0.2s linear 0s;
    background: #ffffffad;
    box-shadow: 0px -2px 3px 4px #ffffffad;
    border-radius: 8rem 8rem 0rem 0rem;
}

/*非首页标题右上框各控件*/
.user-nav .fas
{
    transition: all 0.2s linear 0s;
    margin: 8% 0%;
}
.color-none,.avatar
{
    transition: all 0.2s linear 0s;
}
.color-none:hover,.avatar:hover
{
    transition: all 0.2s linear 0s;
    background: #36dd49;
    box-shadow: 0px 7px 10px #36dd49;
}
.color-none:hover
{
    transition: all 0.2s linear 0s;
    border-radius: 5rem 5rem 5rem 5rem;
}
/*个人操作悬空框*/
.user-nav > .center
{
    transition: all 0.1s linear 0s;
    margin: 8px 20px 0px;
}
.user-nav > .center:hover
{
    transition: all 0.1s linear 0s;
    box-shadow: 0px 0px 10px #17ff2eeb;
}



/*红色、绿色按钮*/
.am-btn,.am-btn-sm
{
    transition:0.15s linear 0s;
    border-radius: 100rem 10rem 100rem 10rem !important;
}
.am-btn:hover,.am-btn-sm:hover
{
    transition:all 0.15s linear 0s;
    background:#fff0;
    box-shadow: 0px 0px 5px 1.5px #29db2d;
    border-color: #29db2d;
    border-radius: 10rem 100rem 10rem 100rem !important;
}

/*蓝色按钮*/
.am-btn-primary
{
    background-color: #29db2d;
    border-color: #29db2d;
}



/*针对标签*/
.am-radius
{
    transition:all 0.15s linear 0s;
    border-radius: 100rem 10rem 100rem 10rem !important;/*弧度*/
}

.am-radius:hover
{
    transition:all 0.15s linear 0s;
    border-radius: 10rem 100rem 10rem 100rem !important;/*弧度*/
}



/*讨论、犇犇类框*/

.am-comment-hd
{
    transition: all 0.2s linear 0s;
    background: rgba(232, 232, 232, .58);
    /*讨论犇犇标题底色*/
    border-top: 1px solid #75c46a;
    /*讨论犇犇上边框*/
    display: -webkit-box;
    display: -webkit-flex;
    display: -ms-flexbox;
    display: flex;
}
.am-comment-hd:hover
{
    transition: all 0.2s linear 0s;
    background: rgba(83, 236, 92, .65);
    /*讨论犇犇标题底色（鼠标在时）*/
}

.am-comment-bd
{
    transition: all 0.2s linear 0s;
    background: rgba(232, 232, 232, .46);
    font-size: 14px;/*讨论犇犇字号*/
}
.am-comment-bd:hover
{
    transition: all 0.2s linear 0s;
    background: rgba(68, 179, 61, .14);
    /*讨论犇犇内容底色（鼠标在时）*/
}



/*{首页比赛、讨论、犇犇框}、{多页面右侧固定框}*/

.am-panel,
.am-comment-main
{
    transition: all 0.2s linear 0s;
    background-color: #00ff091c;
}

.am-panel:hover,.am-comment-main:hover
{
    transition: all 0.2s linear 0s;
    background-color: #f7fff7b0;
    box-shadow: 0px 0px 10px #00dbff;
}

.am-comment-hd
{
     z-index: 0 !important;
}



/*各类框体总管*/
.lg-article
{
    transition: all 0.2s linear 0s;
    background-color: #ebffeb99 !important;
}
.lg-article:hover
{
    transition: all 0.2s linear 0s;
    box-shadow: 0px 0px 10px 2px #37e2d578;
}

/*文本输入框*/
.am-form textarea
,.am-form:focus
{
    transition: all 0.2s linear 0s;
    height: auto;
    background: rgba(142, 166, 140, .26) !important;
}
.am-form textarea:hover
{
    transition: all 0.2s linear 0s;
    background: rgba(125, 164, 120, .35) !important;
}



/*侧边栏*/
#app-sidenav > .color-none:hover
{
    transition: all 0.2s linear 0s;
    border-radius: 5rem 5rem 5rem 5rem;
    background: #276f2f;
    box-shadow: 0px 7px 10px #276f2f;
}

#app-sidenav > .color-none:hover
{
    transition: all 0.2s linear 0s;
    border-radius: 5rem 5rem 5rem 5rem;
    background: #276f2f;
    box-shadow: 0px 7px 10px #276f2f;
}



