a
{
    color: #29b702;
}

/*英文字体*/
.ace_content,
code
{
    font-family: Comic Sans MS;
}



/*底端页码栏*/

/*各按钮*/
body #app-body-new .pagination-centered > ul > li > a
{
    transition: 0.2s;
    opacity: 0.7;
    border-style: outset;
    border-radius: 10rem 10rem 10rem 10rem;
    background-color: #d6ffd7d9;
}
body #app-body-new .pagination-centered > ul > li > a:hover
{
    transition: 0.2s;
    opacity: 0.9;
    background-color: #a6a6ffde;
}
body #app-body-new .pagination-centered > ul > li > a:focus
{
    border-style: inset;
}
/*当前页框*/
body #app-body-new .pagination-centered > .am-pagination > .am-active
{
    visibility: hidden;
}



